IDE_RC qdbAlter::validateAddRangePartition( qcStatement * aStatement )
{
/***********************************************************************
 * Description :
 *
 * Implementation :
 * 
 ***********************************************************************/

    qdTableParseTree        * sParseTree;
    qcmTableInfo            * sTableInfo;
    qdPartitionAttribute    * sAddPartAttr;
    UInt                      sAddKeyCount;
    qcmTableInfo            * sPartInfo;
    smSCN                     sSCN;
    void                    * sHandle;

    qmmValueNode            * sNode;
    UInt                      i;
    qcmColumn               * sTargetColumn  = NULL;
    qcmColumn               * sTableColumn   = NULL;
    idBool                    sIsDefaultTBS  = ID_FALSE;

    qmmValueNode            * sKey           = NULL;
    UInt                      sMaxCount      = 0;

    qcuSqlSourceInfo          sqlInfo;

    sParseTree = (qdTableParseTree *)aStatement->myPlan->parseTree;

    // ------------------------------------------------------
    // 1. ALTER TABLE 구문의 공통적인 validation
    // ------------------------------------------------------
    // 파티션드 테이블에 LOCK(IS)
    // replication 지원 하지 않음.
    IDE_TEST( validateAlterCommon( aStatement,
                                   ID_TRUE ) /* aIsRepCheck */
              != IDE_SUCCESS );

    /* BUG-48290 shard object에 대한 DDL 차단 */
    IDE_TEST( sdi::checkShardObjectForDDL( aStatement, SDI_DDL_TYPE_TABLE ) != IDE_SUCCESS );

    sTableInfo = sParseTree->tableInfo;

    // ------------------------------------------------------
    // 2. 파티션드 테이블인지 체크
    // ------------------------------------------------------
    IDE_TEST_RAISE( ( sTableInfo->tablePartitionType
                      == QCM_NONE_PARTITIONED_TABLE ),
                    ERR_ALTER_TABLE_PARTITION_ON_NONE_PART_TABLE );

    // ------------------------------------------------------
    // 3. AddPart 파티션 validation
    // ------------------------------------------------------
    sAddPartAttr = sParseTree->partTable->partAttr;

    // 이름 중복
    if ( qcmPartition::getPartitionInfo( aStatement,
                                         sTableInfo->tableID,
                                         sAddPartAttr->tablePartName,
                                         & sPartInfo,
                                         & sSCN,
                                         & sHandle )
         == IDE_SUCCESS )
    {
        sqlInfo.setSourceInfo( aStatement,
                               & sAddPartAttr->tablePartName );
        IDE_RAISE( ERR_ALREADY_EXIST_PARTITION_NAME );
    }

    if ( sTableInfo->partitionMethod == QCM_PARTITION_METHOD_RANGE )
    {
        if ( sAddPartAttr->partValuesType == QD_RANGE_VALUES_TYPE )
        {
            // ------------------------------------------------------
            // 4. add partition key cond 확인 - 범위 파티션드
            // ------------------------------------------------------
            // 파티션 추가 기준 값의 개수 체크
            //        파티션 키의 개수보다 작거나 같아야 한다.
            sAddKeyCount = 0;

            for ( sNode = sAddPartAttr->partKeyCond;
                  sNode != NULL;
                  sNode = sNode->next )
            {
                sAddKeyCount++;
            }

            IDE_TEST_RAISE( sTableInfo->partKeyColCount < sAddKeyCount,
                            ERR_TOO_MANY_ADD_COND_VALUE_COUNT );

            // BUG-43042 적용 : MAXVALUE는 파티션 키의 개수와 같아야 한다.
            sKey = sAddPartAttr->partKeyCond;

            while ( sKey != NULL )
            {
                if ( idlOS::strMatch( "MAXVALUE",
                                      8,
                                      sKey->value->position.stmtText + 
                                      sKey->value->position.offset,
                                      sKey->value->position.size ) == 0 )
                {
                    sMaxCount++;
                }
                else
                {
                    // Nothing to do.
                }

                sKey = sKey->next;
            }

            if ( sMaxCount > 0 )
            {
                if ( sMaxCount == sTableInfo->partKeyColCount )
                {
                    // 파티션 키의 개수만큼 MAXVALUE가 있다면 QD_DEFAULT_VALUES_TYPE
                    sAddPartAttr->partValuesType = QD_DEFAULT_VALUES_TYPE;
                    sAddPartAttr->partKeyCond    = NULL;
                }
                else
                {
                    sqlInfo.setSourceInfo( aStatement,
                                           & sAddPartAttr->partKeyCond->value->position );
                    IDE_RAISE( ERR_MAXVALUE_COUNT );
                }
            }
            else
            {
                // Nothing to do.
            }
        }
        else
        {
            // TODO. ALTER TABLE .. ADD PARTITION .. VALUES DEFAULT 구문 지원
            IDE_RAISE( ERR_UNSUPPORTED_SYNTAX );
        }

        // 추가 파티션 키가 적합한지 확인
        IDE_TEST( qdbCommon::checkAddCond( aStatement,
                                           sParseTree )
                  != IDE_SUCCESS );
    }
    else if (sTableInfo->partitionMethod == QCM_PARTITION_METHOD_RANGE_USING_HASH)
    {
        IDE_RAISE( ERR_UNSUPPORTED_SYNTAX );
    }
    else
    {
        // Range partition이 아닌 경우 ADD PARTITION 할 수 없다.
        IDE_RAISE( ERR_ADD_PARTITION_ON_NONE_RANGE_PART_TABLE );
    }

    // ------------------------------------------------------
    // 7. ACCESS 가능 테이블 타입 검사
    // ------------------------------------------------------
    /* PROJ-2359 Table/Partition Access Option */
    IDE_TEST_RAISE( ( ( sTableInfo->tableType == QCM_MVIEW_TABLE ) &&
                      ( sAddPartAttr->accessOption != QCM_ACCESS_OPTION_NONE ) ),
                    ERR_ACCESS_NOT_SUPPORT_MVIEW );

    // ------------------------------------------------------
    // 8. AddPart의 테이블스페이스 validation
    // ------------------------------------------------------
    IDE_TEST( qdbCommon::validateTBSOfPartition( aStatement, sAddPartAttr )
              != IDE_SUCCESS );
    /* PROJ-2639 Altibase Disk Edition */
#ifdef ALTI_CFG_EDITION_DISK
    if ( sTableInfo->tableOwnerID == QC_SYSTEM_USER_ID )
    {
        /* Nothing to do */
    }
    else
    {
        IDE_TEST_RAISE( smiTableSpace::isDiskTableSpaceType( sAddPartAttr->TBSAttr.mType ) == ID_FALSE,
                        ERR_NOT_SUPPORT_MEMORY_VOLATILE_TABLESPACE_IN_DISK_EDITION );
    }
#endif

    /* PROJ-2464 hybrid partitioned table 지원 */
        IDE_TEST( validateTablespaceRestriction( sTableInfo, sAddPartAttr->TBSAttr.mType )
                  != IDE_SUCCESS );

    /* PROJ-2464 hybrid partitioned table 지원 */
    IDE_TEST( validateTableReferenceForPartitions( aStatement,
                                                   sTableInfo,
                                                   NULL,
                                                   sAddPartAttr )
              != IDE_SUCCESS );

    // ------------------------------------------------------
    // 9. LOB column attributes의 validation
    // ------------------------------------------------------
    // 각 파티션별로 LOB 컬럼 정보를 저장하기 위해
    // 테이블 컬럼 정보를 각 테이블 파티션의 컬럼 정보로 복사한다.
    sAddPartAttr->columns = NULL;

    /* PROJ-2464 hybrid partitioned table 지원 */
    IDE_TEST( qdbCommon::copyAndAdjustColumnList( aStatement,
                                                  sTableInfo->TBSType,
                                                  sAddPartAttr->TBSAttr.mType,
                                                  sTableInfo->columns,
                                                  &(sAddPartAttr->columns),
                                                  sTableInfo->columnCount,
                                                  ID_FALSE /* aEnableVariableColumn */ )
              != IDE_SUCCESS );

    // colSpace를 지정한 TBS로 변경한다.
    // 각 파티션의 TBS는 이미 validateTBSOfPartition 함수에서
    // 결정되어 있다.
    for ( i = 0, sTableColumn = sTableInfo->columns, sTargetColumn = sAddPartAttr->columns;
          sTableColumn != NULL ;
          i++, sTableColumn = sTableColumn->next, sTargetColumn = sTargetColumn->next )
    {
        if ( ( sTableColumn->basicInfo->module->flag & MTD_COLUMN_TYPE_MASK )
             == MTD_COLUMN_TYPE_LOB )
        {
            // 사용자가 파티션드 테이블에 LOB TBS를 지정하지 않은 경우
            // 파티션의 TBS를 따른다.
            IDE_TEST( qcm::getIsDefaultTBS( aStatement,
                                            sTableInfo->tableID,
                                            (sTableInfo->tableID * SMI_COLUMN_ID_MAXIMUM) + i,
                                            & sIsDefaultTBS )
                      != IDE_SUCCESS );

            /* PROJ-2464 hybrid partitioned table 지원
             *  - Disk Partition의 경우, Partitioned의 Lob Column TBS(Default TBS)가
             *    지정되어 있으면 Default TBS를 사용한다.
             *  - PROJ-2334 PMT MEMORY PARTITIONED TABLE : Memory Partition의 경우,
             *    Partition TBS를 사용한다.
             */
            /* PROJ-2465 Tablespace Alteration for Table
             *  - 부족한 Default TBS 처리를 추가한다.
             */
            if ( ( sIsDefaultTBS == ID_TRUE ) &&
                 ( smiTableSpace::isDiskTableSpaceType( sAddPartAttr->TBSAttr.mType ) == ID_TRUE ) &&
                 ( smiTableSpace::isDiskTableSpaceType( sTableInfo->TBSType ) == ID_TRUE ) )
            {
                /* Create시 ColSpace에 설정된 Default TBS를 그대로 이용한다. */
                /* Nothing to do */
            }
            else
            {
                sTargetColumn->basicInfo->column.colSpace = sAddPartAttr->TBSAttr.mID;
            }
        }
        else
        {
            sTargetColumn->basicInfo->column.colSpace = sAddPartAttr->TBSAttr.mID;
        }
    }

    // LOB 컬럼의 TBS를 명시하지 않으면, 파티션의 TBS를 따른다.
    // 인자로 넣은 sTableInfo는 형식적으로 넣은 것이고,
    // 실제로 sAddPartAttr의 TBS를 따르게 된다.
    IDE_TEST( qdbCommon::validateLobAttributeList( aStatement,
                                                   sTableInfo,
                                                   sAddPartAttr->columns,
                                                   &(sAddPartAttr->TBSAttr),
                                                   sAddPartAttr->lobAttr )
              != IDE_SUCCESS);

    /* PROJ-2464 hybrid partitioned table 지원 */
    IDE_TEST( validateIndexAlterationForPartition( aStatement,
                                                   sTableInfo,
                                                   sTableInfo,
                                                   sAddPartAttr )
              != IDE_SUCCESS );

    // ------------------------------------------------------
    // 10. 인덱스 파티션을 지정한 경우의 validation
    // ------------------------------------------------------
    if ( sAddPartAttr->alterPart->indexPartAttr != NULL )
    {
        IDE_TEST( checkIndexPartAttrList( aStatement,
                                          sTableInfo,
                                          NULL, /* sSrcPartInfo */
                                          NULL, /* sSrcPartAttr */
                                          sAddPartAttr,
                                          sParseTree->userID )
                  != IDE_SUCCESS );
    }
    else
    {
        /* Nothing to do */
    }

    return IDE_SUCCESS;

    IDE_EXCEPTION(ERR_ALTER_TABLE_PARTITION_ON_NONE_PART_TABLE)
    {
        IDE_SET(ideSetErrorCode(qpERR_ABORT_QDB_CANNOT_ALTER_TABLE_PARTITION_ON_NONE_PART_TABLE));
    }
    IDE_EXCEPTION(ERR_ADD_PARTITION_ON_NONE_RANGE_PART_TABLE)
    {
        IDE_SET(ideSetErrorCode(qpERR_ABORT_QDB_CANNOT_ADD_PARTITION_ON_NONE_RANGE_PART_TABLE));
    }
    IDE_EXCEPTION(ERR_ALREADY_EXIST_PARTITION_NAME)
    {
        (void)sqlInfo.init(aStatement->qmeMem);
        IDE_SET(ideSetErrorCode( qpERR_ABORT_QDB_ALREADY_EXIST_PARTITION_NAME,
                                 sqlInfo.getErrMessage()) );
        (void)sqlInfo.fini();
    }
    IDE_EXCEPTION(ERR_MAXVALUE_COUNT)
    {
        (void)sqlInfo.init(aStatement->qmeMem);
        IDE_SET( ideSetErrorCode( qpERR_ABORT_QCP_SYNTAX,
                                  sqlInfo.getErrMessage() ) );
        (void)sqlInfo.fini();
    }
    IDE_EXCEPTION( ERR_ACCESS_NOT_SUPPORT_MVIEW )
    {
        IDE_SET( ideSetErrorCode( qpERR_ABORT_QDM_ACCESS_NOT_SUPPORT_MVIEW ) );
    }
    IDE_EXCEPTION(ERR_TOO_MANY_ADD_COND_VALUE_COUNT)
    {
        IDE_SET(ideSetErrorCode(qpERR_ABORT_QDB_TOO_MANY_ADD_CONDITION_VALUE));
    }
    IDE_EXCEPTION( ERR_UNSUPPORTED_SYNTAX )
    {
        IDE_SET( ideSetErrorCode(qpERR_ABORT_QCP_NOT_SUPPORTED_SYNTAX) );
    }
    IDE_EXCEPTION_END;

    return IDE_FAILURE;
}

IDE_RC qdbAlter::executeAddRangePartition( qcStatement * aStatement )
{
/***********************************************************************
 *
 * Description :
 *
 * Implementation :
 *
 ***********************************************************************/
    qdTableParseTree        * sParseTree;
    qcmTableInfo            * sTableInfo;
    qcmTableInfo            * sEmptyPartInfo    = NULL;

    UInt                      sPartOrder;
    qdPartitionAttribute    * sAddPartAttr;
    UInt                      sPartitionID;
    smOID                     sPartitionOID;
    qcmTableInfo            * sDstPartInfo      = NULL;
    void                    * sDstPartHandle;
    smSCN                     sDstPartSCN;

    SChar                   * sDstPartMaxVal;

    SChar                     sEmptyPartMinVal[QC_MAX_PARTKEY_COND_VALUE_LEN+1];
    SChar                     sEmptyPartMaxVal[QC_MAX_PARTKEY_COND_VALUE_LEN+1];

    IDU_FIT_POINT("qdbAlter::executeAddRangePartition::malloc1");
    IDE_TEST(aStatement->qmxMem->cralloc(
                 ID_SIZEOF(SChar) * (QC_MAX_PARTKEY_COND_VALUE_LEN+1),
                 (void**)&sDstPartMaxVal) != IDE_SUCCESS);

    idlOS::memset( sEmptyPartMinVal, 0x00, QC_MAX_PARTKEY_COND_VALUE_LEN+1 );
    idlOS::memset( sEmptyPartMaxVal, 0x00, QC_MAX_PARTKEY_COND_VALUE_LEN+1 );

    sParseTree = (qdTableParseTree *)aStatement->myPlan->parseTree;
    sTableInfo = sParseTree->tableInfo;

    IDU_FIT_POINT( "qdbAlter::executeAddRangePartition::beforeXLock" );

    // BUG-42329 : 파티션드 테이블에 LOCK(IX)
    // empty partition 에 LOCK(X)
    IDE_TEST( qcmPartition::validateAndLockTableAndPartitions(
                  aStatement,
                  sParseTree->tableHandle,
                  sParseTree->tableSCN,
                  sParseTree->partTable->partInfoList,
                  SMI_TABLE_LOCK_IX,
                  ID_TRUE ) //aIsSetViewSCN
              != IDE_SUCCESS );

    // lock을 잡은 이후에 해야합니다.
    sAddPartAttr      = sParseTree->partTable->partAttr;
    sEmptyPartInfo    = sParseTree->partTable->partInfoList->partitionInfo;

    //-----------------------------------------------------
    // 1. 파티션 키 조건 값
    //-----------------------------------------------------
    // 1-1. 추가 파티션의 MIN = 현재 empty 파티션의 MIN
    IDE_TEST( qcmPartition::getPartMinMaxValue( QC_SMI_STMT( aStatement ),
                                                sEmptyPartInfo->partitionID,
                                                sEmptyPartMinVal,
                                                sEmptyPartMaxVal )
              != IDE_SUCCESS );

    // 1-2. AddPart의 추가 기준값은 PARTITION_MAX_VALUE이다.
    IDE_TEST( qdbCommon::getPartCondValueFromParseTree( aStatement,
                                                        sAddPartAttr,
                                                        sDstPartMaxVal )
              != IDE_SUCCESS );

    // 1-3. 범위, 리스트 파티션드 테이블에는 파티션 순서 없음
    sPartOrder = QDB_NO_PARTITION_ORDER;

    // ------------------------------------------------
    // 2. 파티션 생성 및 메타 정보 입력
    // ------------------------------------------------
    IDE_TEST( qdbCommon::createTablePartition( aStatement,
                                               sParseTree,
                                               sTableInfo,
                                               sAddPartAttr,
                                               sEmptyPartMinVal,
                                               sDstPartMaxVal,
                                               sPartOrder,
                                               &sPartitionID,
                                               &sPartitionOID)
              != IDE_SUCCESS );

    // 새로 ADD되는 파티션의 메타 캐시 생성
    IDE_TEST( qcmPartition::makeAndSetQcmPartitionInfo( QC_SMI_STMT( aStatement ),
                                                        sPartitionID,
                                                        sPartitionOID,
                                                        sTableInfo,
                                                        NULL )
              != IDE_SUCCESS );

    // Dst PartInfo를 가져온다.
    IDE_TEST( qcmPartition::getPartitionInfo( aStatement,
                                              sTableInfo->tableID,
                                              sAddPartAttr->tablePartName,
                                              & sDstPartInfo,
                                              & sDstPartSCN,
                                              & sDstPartHandle )
              != IDE_SUCCESS );

    // ------------------------------------------------
    // 3. AddPart의 Index를 생성한다.
    // ------------------------------------------------
    if ( sTableInfo->indices != NULL )
    {
        IDE_TEST( qdx::createAllIndexOfTablePart( aStatement,
                                                  sTableInfo,
                                                  sDstPartInfo,
                                                  sAddPartAttr->alterPart->indexPartAttr )
                  != IDE_SUCCESS );
    }
    else
    {
        // Nothing to do.
    }

    // ------------------------------------------------
    // 4. Empty partition의 min_value를 AddPart의 max_value로 변경
    // ------------------------------------------------
    IDE_TEST( qdbCommon::updatePartMinValueOfTablePartMeta( aStatement,
                                                            sEmptyPartInfo->partitionID,
                                                            sDstPartMaxVal )
              != IDE_SUCCESS );

    // ------------------------------------------------
    // 5. 메타캐시 재구성
    // ------------------------------------------------
    // empty partition touch
    IDE_TEST( qcmPartition::touchPartition( QC_SMI_STMT( aStatement ),
                                            sEmptyPartInfo->partitionID )
              != IDE_SUCCESS);

    IDU_FIT_POINT( "qdbAlter::executeAddRangePartition::afterTouch" );

    if ( sDstPartInfo != NULL )
    {
        (void)qcmPartition::destroyQcmPartitionInfo( sDstPartInfo );

        sDstPartInfo = NULL;
    }
    else
    {
        /* Nothing to do */
    }

    // 새로 ADD되는 파티션의 메타 캐시 생성
    IDE_TEST( qcmPartition::makeAndSetQcmPartitionInfo( QC_SMI_STMT( aStatement ),
                                                        sPartitionID,
                                                        sPartitionOID,
                                                        sTableInfo,
                                                        NULL )
              != IDE_SUCCESS );

    IDE_TEST( smiGetTableTempInfo( smiGetTable( sPartitionOID ),
                                   (void**)&sDstPartInfo )
              != IDE_SUCCESS);

    // SYS_TABLES_의 LAST_DDL_TIME 변경
    IDE_TEST( qdbCommon::updateTableSpecFromMeta( aStatement,
                                                  sParseTree->userName,
                                                  sParseTree->tableName,
                                                  sTableInfo->tableID,
                                                  smiGetTableId(sTableInfo->tableHandle),
                                                  sTableInfo->columnCount,
                                                  sTableInfo->parallelDegree )
              != IDE_SUCCESS );

    IDE_TEST(qcmView::setInvalidViewOfRelated( aStatement,
                                               sParseTree->userID,
                                               sTableInfo->name,
                                               idlOS::strlen((SChar*)sTableInfo->name),
                                               QS_TABLE)
             != IDE_SUCCESS);

    IDE_TEST(qcmView::recompileAndSetValidViewOfRelated( aStatement,
                                                         sParseTree->userID,
                                                         sTableInfo->name,
                                                         idlOS::strlen((SChar*)sTableInfo->name),
                                                         QS_TABLE)
             != IDE_SUCCESS);

    return IDE_SUCCESS;

    IDE_EXCEPTION_END;

    if ( sDstPartInfo != NULL )
    {
        (void)qcmPartition::destroyQcmPartitionInfo( sDstPartInfo );
    }

    return IDE_FAILURE;
}
